    r12 = *ivar_offset(_contentView);
    rdi = *(rbx + r12);
    if (rdi != r14) goto loc_885c9;

....

loc_885c9:
    [rdi removeFromSuperview];
    *(rbx + r12) = r14;
    rsi = rbx->_borderView;
    if (rsi != 0x0) {
            rax = [&var_50 frame];
    }
    else {
            var_40 = intrinsic_movaps(var_40, 0x0);
            var_50 = intrinsic_movaps(var_50, 0x0);
    }
    rax = [rbx styleMask];
    rax = [&var_70 contentRectForFrameRect:rbx styleMask:rax, r8];
    if ((__NSConstraintBasedLayout() != 0x0) && ([rbx _layoutEngine] != 0x0)) {
            [r14 setAutoresizingMask:0x12];
    }
    [r14 setTranslatesAutoresizingMaskIntoConstraints:0x1];
    [*(rbx + r12) setFrame:0x1];
    if (((__NSViewLayerBackWindowFrame() == 0x0) || ([*(rbx + r12) wantsLayer] == 0x0)) || ([rbx->_borderView wantsUpdateLayer] == 0x0)) goto loc_88753; <---------------------------------------------- important part

loc_8870e:
