    visible: true
    width: 640
    height: 480

    TextField {
        anchors.centerIn: parent
        color: "white"
        placeholderText: "Search..."
        placeholderTextColor: "#aabbccdd" // <===
        background: Rectangle {
            color: "black"
        }
    }
}
